mapper.update:
update employee
set name = #{name}, password = #{password}, age = #{age} , position_id = #{positionId} , department_id = #{departmentId}, phone = #{phone}, email = #{email}
where employee_id = #{employeeId}

mapper.select:
select  employee_id,name,age,position_name,department_name,phone,email,permissions from employee left join hr.position p on employee.position_id = p.position_id left join hr.department d on p.department_id = d.department_id

    and employee_id = #{employeeId}
     and employee.department_id = #{departmentId}
